public static function loadProductBlock()
{
    global $ARGS, $TEMPLATE, $ORDER, $LANGUAGE;

    $rows = [];
    $activeExtraDevices = [];
    $totalRent = 0;
    $totalContract = 0;
    $totalDeposit = 0;

    if (isset($ORDER)) {
        $orderitems = $ORDER->orderitems->get();
        foreach ($orderitems as $item) {
            $rows[$item->c['id']]['orderitem']['id'] = $item->c['id'];
            $rows[$item->c['id']]['orderitem']['amount'] = $item->c['amount'];

            $product = $item->product->get();
            if ($product->c['type'] == 'pindevice') {
                $rows[$item->c['id']]['product']['id'] = $product->c['id'];
                $rows[$item->c['id']]['product']['type'] = $product->c['type'];
                $rows[$item->c['id']]['product']['icon'] = $product->getIcon();
                $rows[$item->c['id']]['product']['name'] = $product->getName();
                $rows[$item->c['id']]['product']['articleCode'] = $product->c['article_code'];
                $rows[$item->c['id']]['product']['pricePerDay'] = $product->getPricePerDay() * $item->c['amount'];
                $rows[$item->c['id']]['product']['transactionCosts'] = $product->getTransactionCostsPin();

                $totalRent += $rows[$item->c['id']]['product']['pricePerDay'];
                $totalContract += $product->getContractTransportCosts() * $item->c['amount'];
                $totalDeposit += $product->getDeposit() * $item->c['amount'];
            } elseif ($product->c['type'] == 'extra') {
                $activeExtraDevices[$product->c['article_code']] = $product;
            }
        }
    }
    $TEMPLATE->assign('rows', $rows);
    $TEMPLATE->assign('activeExtraDevices', $activeExtraDevices);
    $TEMPLATE->assign('totalRent', $totalRent);
    $TEMPLATE->assign('totalContract', $totalContract);
    $TEMPLATE->assign('totalDeposit', $totalDeposit);

    $search = Models\Product::search(['available_'.$LANGUAGE => 'y', 'type'=>'extra'], [], false, false, []);
    $searchResults = Models\Product::loadMany($search['results']);
    $extraDevices = [];
    foreach ($searchResults as $product) {
        $extraDevices[$product->c['article_code']] = $product;
    }
    $TEMPLATE->assign('extraDevices', $extraDevices);
}

