```python
from abc import ABC, abstractmethod
from itertools import combinations


class MeetInTheMiddleTemplate(ABC):

    def meet_in_the_middle(self, data):
        n = len(data)
        half = n // 2
        left_half = data[:half]
        right_half = data[half:]

        left_subsets = self.generate_subsets(left_half)
        right_subsets = self.generate_subsets(right_half)

        left_processed = self.process_subsets(left_subsets)
        right_processed = self.process_subsets(right_subsets)

        result = self.find_meeting_point(left_processed, right_processed)
        return result

    def generate_subsets(self, data):
        subsets = []
        for i in range(len(data) + 1):
            subsets.extend(list(combinations(data, i)))
        return subsets

    @abstractmethod
    def process_subsets(self, subsets):
        pass

    @abstractmethod
    def find_meeting_point(self, left_processed, right_processed):
        pass


class MyMeetInTheMiddle(MeetInTheMiddleTemplate):

    def process_subsets(self, subsets):
        subset_sums = {}
        for subset in subsets:
            subset_sum = sum(subset)
            if subset_sum not in subset_sums:
                subset_sums[subset_sum] = []
            subset_sums[subset_sum].append(subset)
        return subset_sums

    def find_meeting_point(self, left_processed, right_processed):
        for key in left_processed:
            if key in right_processed:
                return left_processed[key], right_processed[key]
        return None


if __name__ == "__main__":
    data = [1, 2, 3, 4, 5, 6, 7, 8]
    mitm_instance = MyMeetInTheMiddle()
    result = mitm_instance.meet_in_the_middle(data)
    print(f"Subsets with equal sum: {result}")
```

